import './BannerPrincipal.css';
import Icones from './Icones';
import CopiarClipBoard from './CopiarClipBoard';
import TypingTextAnimation from 'react-typing-text-animation'
import FadeIn from 'react-fade-in';

export default function BannerPrincipal(){
    const copyToClipboard = CopiarClipBoard();

    return(
        <div className="containerGeral">
            <div className="itensContainerGeral">
                <div className="textoTituloApresentacao">
                    <h2><TypingTextAnimation delay={50} text='Olá, meu nome é Filipe e eu sou um...' /></h2>
                </div> {/* div textoTituloApresentacao */}

                <div className="subContainerGeral">
                    <div className="tituloPrincipalA w50">
                        <FadeIn delay={1900} transitionDuration={1000}><h1>React</h1></FadeIn>
                    </div> {/* div tituloPrincipalA */}

                    <div className="desktopIcone">
                        <Icones />
                    </div>
                </div> {/* div subContainerGeral */}

                <div className="tituloPrincipalB">
                    <FadeIn delay={2500} transitionDuration={1000}><h1>Developer</h1></FadeIn>
                </div> {/* div tituloPrincipalB */}

                <div className="containerEmail">

                    <div className="paragrafoDescricao w50">
                        <FadeIn delay={6000}transitionDuration={1000}>
                            <p>Sou um Desenvolvedor Web absolutamente dedicado e entusiasmado pelo que faço!
                            A pergunta <strong><i>"O que você faria de graça (se fosse possível)?"</i></strong> seria facilmente respondida
                            pelo tempo dedicado ao desenvolvimento de sistemas que prezam pela melhor experiência de usuário,
                            cuidado com a responsividade além de <i>designs</i> elegantes e dinâmicos.</p>
                        </FadeIn>
                    </div>{/* div paragrafoDescricao */}

                    <FadeIn delay={6000}transitionDuration={1000}>
                        <div className="botaoEmail w50">
                            <div className="emailTitulo"><h3>Copie meu email abaixo:</h3></div>
                            <button className="campoEmail" onClick={() => copyToClipboard('sfilipe41@outlook.com')}>Copiar email para área de transferência</button>
                        </div>
                    </FadeIn>
                </div>
            </div> {/* div itensContainerGeral */}

        </div> //div-containerGeral
    );
}
