import { useCallback } from 'react';
import MainBannerPage from './MainBannerPage';
import React, { useState } from 'react';


export default function CopiarClipBoard() {
  const [copied, setCopied] = useState(false);
  const copyToClipboard = useCallback(async (text) => {
    try {
      await navigator.clipboard.writeText(text);
      console.log('Texto copiado para a área de transferência');
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.log('Falha ao copiar o texto', err);
    }
  }, []);
  return (
    <div>
      <button
        id="email"
        className="campoEmail"
        onClick={() => copyToClipboard('sfilipe41@outlook.com')}
        disabled={copied} // Desativa o botão se o email já foi copiado
      >
        {copied ? 'Email copiado com sucesso' : 'Copiar email para área de transferência'}
      </button>
    </div>
  );
}
