import '../estilos/App.css';
import Header from './Header';
import MainBannerPage from './MainBannerPage';
import SkillsBanner from './SkillsBanner';
import ProjectBanner from './ProjectBanner'
import FadeIn from 'react-fade-in';
import React, { useRef } from 'react';


function App() {
  const inicioRef = useRef(null);
  const habilidadesRef = useRef(null);
  const projetosRef = useRef(null);

  const scrollToInicio = () => {
    inicioRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  const scrollToHabilidades = () => {
    if (habilidadesRef.current !== null) {
      habilidadesRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const scrollToProjetos = () => {
    projetosRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="App">
      <Header 
        scrollToInicio={scrollToInicio}
        scrollToHabilidades={scrollToHabilidades}
        scrollToProjetos={scrollToProjetos}
      />
      <MainBannerPage />
      <FadeIn delay={7000} transitionDuration={1000}><SkillsBanner ref={habilidadesRef} /></FadeIn>
      <ProjectBanner ref={projetosRef} />
    </div>
  );
}

export default App;
