import '../estilos/App.css';
import Header from './Header';
import MainBannerPage from './MainBannerPage';
import SkillsBanner from './SkillsBanner';
import ProjectBanner from './ProjectBanner'
import FadeIn from 'react-fade-in';
import { useRef } from 'react';

function App() {
  const inicio = useRef(null)
  const skills = useRef(null)
  const projects = useRef(null)

  const scrollToSection = (elementRef) => {
    if (elementRef.current) { // Verifica se o ref atual está definido
      window.scrollTo({
        top: elementRef.current.offsetTop,
        behavior:'smooth'
      })
    } else {
      console.error("O elemento referenciado ainda não foi montado no DOM.");
    }
  }

  return (
    <div className="App">
      <Header
        inicio={inicio} 
        skills={skills} 
        projects={projects} 
        scrollToSection={scrollToSection} />
      <MainBannerPage ref={inicio} />
      <FadeIn delay={7000} transitionDuration={1000}><SkillsBanner ref={skills} /></FadeIn>
      <ProjectBanner ref={projects} />
    </div>
  );
}

export default App;
